CREATE TABLE  `emails` (    `id` int NOT NULL AUTO_INCREMENT,    `uniqid` varchar(255) NOT NULL,    `time` varchar(255) NOT NULL,    `name` varchar(255) NOT NULL,    `email` varchar(255) NOT NULL,    `subject` varchar(255) NOT NULL,    `body_text` mediumtext NOT NULL,    `body_html` mediumtext NOT NULL,    `account_id` int DEFAULT NULL,    `folder` varchar(255) DEFAULT NULL,    PRIMARY KEY (`id`)  ) ENGINE = InnoDB AUTO_INCREMENT = 217 DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci  CREATE TABLE  `files` (    `id` int NOT NULL AUTO_INCREMENT,    `email_id` varchar(255) NOT NULL,    `filename` varchar(255) NOT NULL,    PRIMARY KEY (`id`)  ) ENGINE = InnoDB AUTO_INCREMENT = 157 DEFAULT CHARSET = latin1  CREATE TABLE  `folders` (    `id` int unsigned NOT NULL AUTO_INCREMENT,    `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,    `account_id` int DEFAULT NULL,    `folder_name` varchar(255) DEFAULT NULL,    PRIMARY KEY (`id`)  ) ENGINE = InnoDB AUTO_INCREMENT = 95 DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci  CREATE TABLE  `sync_credentials` (    `id` int unsigned NOT NULL AUTO_INCREMENT,    `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,    `mail` varchar(255) DEFAULT NULL,    `password` varchar(255) DEFAULT NULL,    `token` varchar(255) DEFAULT NULL,    `token_expiration` varchar(255) DEFAULT NULL,    PRIMARY KEY (`id`)  ) ENGINE = InnoDB AUTO_INCREMENT = 2 DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci